mapper.select:
SELECT

FROM sys_user a

WHERE a.id = #{id}

SELECT

FROM sys_user a

WHERE a.account = #{account} and password = #{password}

SELECT

FROM sys_user a


    a.del_flag = #{DEL_FLAG_NORMAL}

        AND a.code = #{code}


        AND a.account = #{account}


        AND a.password = #{password}



        AND a.user_name LIKE
        '%'||#{name}||'%'
        '%'+#{name}+'%'
        concat('%',#{name},'%')

mapper.update:
UPDATE sys_user SET
password = #{password},
sysversion = sysversion+1,
update_by = #{updateBy},
update_date = #{updateDate}
WHERE id = #{id}

    AND sysversion = #{sysversion}

UPDATE sys_user SET
del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
